/* eslint-disable */
import React from "react";
import axios from "axios";
import { Select } from "antd";
const { Option } = Select;

function onChange(value) {
  console.log(`selected ${value}`);
}

function onBlur() {
  console.log('blur');
}

function onFocus() {
  console.log('focus');
}

function onSearch(val) {
  console.log('search:', val);
}
class Mock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      gData: null,
      pData: null,
    };
  }

  getDate1 = () => {
    axios
      .get("/getdata1", {
        params: {
          id: "123",
        },
      })
      .then((res) => {
        //console.log(res.data)
        this.setState({
          gData: res.data.message,
        });
      })
      .catch((err) => {
        //console.log(err)
      });
  };

  getDate2 = () => {
    axios
      .post("/postdata1", {
        params: {
          name: "jack",
        },
      })
      .then((res) => {
        //console.log(res.data)
        this.setState({
          pData: res.data.message,
        });
      })
      .catch((err) => {
        // console.log(err)
      });
  };

  render() {
    return (
    <div>
        {/* <h3>数据展示</h3>
                <p>get：{ this.state.gData == null ? '无' :  this.state.gData }</p>
                <p>post: { this.state.pData == null ? '无' :  this.state.pData } </p>

                <button onClick={ this.getDate1}>点击获取(Get)Mock数据</button>
                <br /><br />
                <button onClick={ this.getDate2 } >点击获取(Post)Mock数据</button> */}
        <Select
          showSearch
          style={{ width: 200 }}
          placeholder="Select a person"
          optionFilterProp="children"
          onChange={onChange}
          onFocus={onFocus}
          onBlur={onBlur}
          onSearch={onSearch}
          filterOption={(input, option) =>
            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          }
        >
          <Option value="jack">Jack</Option>
          <Option value="lucy">Lucy</Option>
          <Option value="tom">Tom</Option>
        </Select>

        </div>
    );
  }
}

export default Mock;
